float drawLineX( vec2 p1, vec2 p2)
{
    return distance(p1.x, p2.x);
}
float drawLineY( vec2 p1, vec2 p2)
{
    return distance(p1.y, p2.y);
}
float drawSquare( vec2 p1, vec2 p2 )
{
    return distance(distance(p1.x, p2.x),distance(p1.y,p2.y));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    if(uv.y <= 0.7) fragColor = vec4(-uv.x/-uv.y) - 0.7 + texture(iChannel1, fract(vec3(-uv, 1.0))/uv.y) +
        vec4(0.5, 0.2, 0.1, 1.0);
    else fragColor = texture(iChannel0, (vec3(-uv, 1.0))/uv.x);
    float line = drawLineX(  vec2(0.5), vec2(0.85) ) + 0.1;
    float line2 = drawLineX( vec2(0.5), vec2(1.02) ) + 0.1;
    float line3 = drawLineY( vec2(0.6), vec2(0.85) ) + 0.1;
    float line4 = drawLineY( vec2(0.4), vec2(1.02) );
    if(uv.x >= line - 0.01 && uv.x <= line + 0.01 && uv.y <= 0.9 && uv.y >= 0.1)
        fragColor = vec4(((line/line2)+(line2+line3)*(line3*line4)+(line4/line))/
                    ((line/line4)-(line4-line3)+(line3/line2)/(line2*line)))*uv.x/uv.y+vec4(1.0,0.0,0.0,1.0);
    if(uv.x >= line2 - 0.01 && uv.x <= line2 + 0.01 && uv.y <= 0.9 && uv.y >= 0.1)
        fragColor = vec4(((line/line2)+(line2+line3)*(line3*line4)+(line4/line))/
                    ((line/line4)-(line4-line3)+(line3/line2)/(line2*line)))*uv.x/uv.y+vec4(1.0,0.0,0.0,1.0);

    if(uv.y >= line3 - 0.01 && uv.y <= line3 + 0.01 && uv.x <= 0.78 && uv.x >= 0.28)
        fragColor = vec4(((line/line2)+(line2+line3)*(line3*line4)+(line4/line))/
                    ((line/line4)-(line4-line3)+(line3/line2)/(line2*line)))*uv.x/uv.y+vec4(1.0,0.2,0.0,1.0);
    if(uv.y >= line4 - 0.01 && uv.y <= line4 + 0.01 && uv.x <= 0.78 && uv.x >= 0.28)
	fragColor = vec4(((line/line2)+(line2+line3)*(line3*line4)+(line4/line))/
                    ((line/line4)-(line4-line3)+(line3/line2)/(line2*line)))*uv.x/uv.y+vec4(1.0,0.0,0.0,1.0);
}
