void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 samp = texture(iChannel0, uv);
	fragColor = vec4(clamp(.5+(samp.x/samp.y-0.5)*(pow(10.,2.*uv.x)),0.,1.));
}

vec4 hash(vec4 a) {
	return fract(abs(sin(a.ywxz*766.345)+cos(normalize(a)*4972.92855))*2048.97435+abs(a.wxyz)*.2735);
}

#define PI 3.14159265358979

#define rand1(u) fract((fract(u.x*.87654321)+fract(u.y*.987654321)+iTime)*PI)
//#define rand1(u) hash(u.xyxy*.97234+float(iFrame)*.13131).x
#define rand2(u) texelFetch(iChannel1,ivec2(mod(u+float(iFrame)*vec2(299.89723,33.87234),1024.)),0).x

void mainImage( out vec4 o, in vec2 u)
{
    if (iFrame == 0) {
        o = vec4(0);
        return;
    }

    float div = u.y-iResolution.y*.5,v;
    if (abs(div) < 1.) v = .3;
    else if (div > 0.) v = rand1(u);
    else v = rand2(u);
	o = texelFetch(iChannel0,ivec2(u),0)+vec4(v,1,0,0);
}
