#define S(a) max(0., 1.-(a-x)*(a-x)*2500.)

void mainImage( out vec4 O,  vec2 U )
{
	U /= iResolution.xy;

    O -= O;
    float x = fract(4.*U.x)-.375;

    abs(U.y-.5) <.4
    	? O +=  S() + S( abs(cos(4.*U.y-.8))/5. ) ,   // (U.y-.2)*3.9 closer to original
          O *=  1.-length(U-.5) 
        : O;
}
