// en-/disable the whole effect
# define sm true
// for horizontal screen door effect set to 1.0
# define hsde 1.0
// in case we want to lose less brightness
# define black vec3(.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // vec3 color = vec3(uv,0.5+0.5*sin(iTime));
    vec3 color = texture(iChannel0, uv).rgb;

    // rgb subpixel matrix
    if (sm) {
        if (mod(fragCoord.x, 3.0+hsde) <1.0) {
            color = vec3(color.r, 0, 0);
        } else if (mod(fragCoord.x, 3.0+hsde) <2.0) {
            color = vec3(0, color.g, 0);
        } else if (mod(fragCoord.x, 3.0+hsde) <3.0) {
            color = vec3(0, 0, color.b);
        } else {
            // screen door effect horizontal
            color = black;
        }
    }

    // screen door effect vertical
    if (sm && mod(fragCoord.y, 3.0+hsde) < 1.0) {
        color = black;
    }

	fragColor = vec4(color,1.0);
}
