#define PI 3.14159265359
#define twoPI PI*2.0
#define HashMe(a) fract(sin(a)*13.0) 

float iqNoiseM(vec3 x) {
    vec3 p = floor(x );
    vec3 f = fract(x);


    f = f*f*(3.0-2.0*f);
   //  f = f*f*f*(f*(f*6.0-15.0)+10.0);
    float c1 = 883.0;
    float c2 = 971.0;
    float c3 = 127.0;
    float n = p.x*c3 + p.y*c2+ c1*p.z;
    return mix(
        mix(
            mix(HashMe(n+0.0),HashMe(n+ c3),f.x),
            mix(HashMe(n+c2),HashMe(n+c2+ c3),f.x),
            f.y),
        mix(
            mix(HashMe(n+c1),HashMe(n+c1+ c3),f.x),
            mix(HashMe(n+c1+c2),HashMe(n+c1+c2+ c3),f.x),
            f.y),
        f.z);
}

float PatternSins(vec3 x){

    vec3 x2 = (x)+ sin(x)*twoPI;
    return iqNoiseM(x2);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    float n = PatternSins(vec3(0.0, uv*20.0) * cos(iTime*0.41/3.0)*3.0 + 
                           iTime*0.41);
    vec3 color = mix(vec3(0.4,0.18,0.2), vec3(0.23,.9,.7), n)*n;
	fragColor = vec4(color, 1.0);
}
