void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 s = texture( iChannel0, vec2(fragCoord.x/iResolution.x,fragCoord.y/iResolution.y) );

    s *= mix(vec4(1.),vec4(0.0),  abs( clamp(fragCoord.y/iResolution.y,.4,.6) - 0.5) );

	vec3 test = mix( vec3(1.),vec3(0.0), ceil( clamp(fragCoord.y/iResolution.y - s.r, 0., 1. ) ) );   

	fragColor = vec4(test,1);
}
