float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float random(vec2 st, float seed) {
    return fract(sin(seed + dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float noise (vec2 c, float density, float seed) {
    vec2 cu = fract(c*density);
    vec2 ci = floor(c*density);
    float bl = random(ci, seed);
    float br = random(ci + vec2(1.0, 0.0), seed);
    float tl = random(ci + vec2(0.0, 1.0), seed);
    float tr = random(ci + vec2(1.0, 1.0), seed);
    cu = smoothstep(0., 1., cu);

    float b = mix(bl, br, cu.x);
    float t = mix(tl, tr, cu.x);
    float v = mix(b, t, cu.y);
    return v;
}

float noise (vec2 c, float density) {
    vec2 cu = fract(c*density);
    vec2 ci = floor(c*density);
    float bl = random(ci);
    float br = random(ci + vec2(1.0, 0.0));
    float tl = random(ci + vec2(0.0, 1.0));
    float tr = random(ci + vec2(1.0, 1.0));
    cu = smoothstep(0., 1., cu);
    float b = mix(bl, br, cu.x);
    float t = mix(tl, tr, cu.x);
    float v = mix(b, t, cu.y);
    return v;
}

float line (vec2 c, float width) {
    float smoothing = .04;
    return smoothstep(.5 - width, .5 - width + smoothing, c.y) - smoothstep(.5 + width - smoothing, .5 + width, c.y);
}

mat2 rotate (float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

vec2 twist (vec2 c, float angle) {
    return (c - .5)*rotate(angle) + .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = vec2(iTime/20.0, 0.0) + fragCoord.xy/iResolution.xy;
    vec2 sti = floor(st);
    float noise_freq = 1.8;
    float noise_smoothing = 1.0;
    float line_count = 20.0;
    float line_width = .2;


    st = twist(st, pow(noise(st, noise_freq), noise_smoothing));
    vec2 c = fract(st*vec2(1.0, line_count));
    vec3 background = vec3(.4, .19, .04);
    vec3 foreground = vec3(.29, .11, .04);
    fragColor = vec4(mix(background, foreground, line(c, line_width)), 1.0);
}
