void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    st.x -= 0.4;

    vec3 color = vec3(0.);

    float f;

    st -=0.5;
    float r = length(st)*1.;


    f = distance(vec2(0.), st.xy);
    float d = f;


    float a = atan(st.x, st.y)*3. + tan((d*5.+iTime));
    f = sin(a) + f;
    f = sin(a*2.) * cos(a*1.);


    vec3 colA = vec3(st.x,st.y, st.x);
	vec3 colB = vec3(st.y,f,0.034);

    colA = vec3(0.);
    //colB = vec3(1.);
    vec3 gradient = mix(colA, colB, distance(vec2(0.), st.xy) * 3.);

    color = vec3(1.-f * gradient);
    if(color.r > 0.99){
        color = 1. - gradient + .6;
    }

    color.r = color.g = color.b;
    color.rgb -= .5;
    color.rgb = 1. - color;

    color /= .5;

    fragColor = vec4(color,1.0);
}
