vec2 R;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
void mainImage( out vec4 C, in vec2 U )
{
    R = iResolution.xy;
    C = T(U);
    C = 0.5+0.5*sin(5.*C.w+8.*C.z*vec4(1.1,1.5,1.8,1));
}

vec2 R;float N;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
float X (vec2 U1, vec2 U, inout vec4 Q, in vec2 r) {
    vec2 V1 = U1 + r, V  = V1 - T(V1).xy;
    float P = T (V).z, rr = length(r);
    Q.xy -= r*(P-Q.z)/rr/N;
    return (length(V-U)-rr+P)/N;
}

void mainImage( out vec4 Q, in vec2 U )
{   R = iResolution.xy;
 	vec2 U1 = U; U -= T(U).xy;
 	float P = 0.; Q = T(U);
 	N = 4.;
 	P += X (U1,U,Q, vec2( 1, 0) );
 	P += X (U1,U,Q, vec2( 0,-1) );
 	P += X (U1,U,Q, vec2(-1, 0) );
 	P += X (U1,U,Q, vec2( 0, 1) );
 	Q.z = P;
 	if (iFrame < 1) Q = vec4(0);
    if (U.x < 1.||U.y < 1.||R.x-U.x < 1.||R.y-U.y < 1.) Q.xy *= 0.;

 	vec2 b = abs(U-0.5*R.xy);
    float l = max(b.x,b.y);
 	if (l > 40. && l < 50. && !(iFrame>50&&U.x>0.5*R.x&&abs(U.y-0.5*R.y)<2.)) Q = Q*0.9+0.1*vec4(0,0,vec2(1)*(50.-l)/10.);

}

