vec4 circle(vec2 pos, vec2 center, float radius, vec4 color)
{  

    return smoothstep(2.,0.,length(pos - center) - radius) * color;

} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord;
    vec4 para = vec4(0.5,0.5,200,0);
    vec4 color = vec4(0.5,0.5,0.5,0);
	fragColor =  circle(pos, para.xy * iResolution.xy, para.z, color);  
}
