#define R     0.1
#define RSQ 0.01

#define ls(A) dot(A, A)
#define lowerd(D) x = dot(D,D); if (x<d) { d=x; minn = D; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - iResolution.xy * 0.5 ) /iResolution.y;
    vec3 lookAt = normalize(vec3(cos(iTime * 0.0745), sin(iTime * 0.63435), cos(iTime * 0.3423))),
        	up = normalize(cross(lookAt, vec3(sin(iTime * 0.232), sin(iTime* 0.3463), cos(iTime * 0.2343)))),
        	xAxis = cross(up, lookAt);
	vec3 ray = normalize(vec3(uv, 0.8)),
        start = vec3(0.5, 0.5, iTime * 1.),
        cam = start,
        light = normalize(vec3(0.8, 0.3, 0.5));
    ray = ray.x * xAxis + ray.y * up + ray.z * lookAt;

    vec2 minn;
    for (int i = 0; i < 50; ++i) {
        float d = 1.0, x;
        vec2[] slices = vec2[] (cam.xy, cam.yz, cam.zx);
        for (int s = 0; s < 3; ++s) {
            vec2 slice = slices[s],
                toLast = mod(slice, 1.0),
                toNext = 1.0 - toLast;
            lowerd(toLast);
            lowerd(toNext);
            lowerd(vec2(toLast.x, toNext.y));
            lowerd(vec2(toNext.x, toLast.y));
        }
        if (d <= RSQ) {
            break;
        }
        float l = sqrt(d) - R;
        cam += ray * l;
    }

    fragColor = texture(iChannel0, cam.xy + cam.yz)
        * (atan(minn.y, minn.x) * 0.5 + 1.)
        / length(cam - start);
}
