void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 p = 256.0 * fragCoord.xy/iResolution.xy;

   float level = 5.0;
   p = p / pow(2.0,level); // number of rects in a row = 2^(8 - level)

   vec2 a = floor(p); // left-bottom corner of local rect
   vec2 b = fract(p); // local coordinate inside local rect [0,1]x[0,1]

   // assign the color
   float color = mod( a.x + a.y, 2.0 );

   fragColor = vec4( color, color, color, 1.0 );
}
