void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//init to middle of screen
    vec2 p = fragCoord.xy / iResolution.xy;
    //alias time
    float time = iTime;
    //init c
	vec3 c=vec3(0);
    for(int i=0;i<160;i++){
        //create a position for each point
        float t = 2.*3.14*float(i)/160. * sin(time*0.5);
        //sohcahtoa
        float x = -cos(t)+2.;
        //float x = 1./80. * float(i) + 1.; 
        float y = sin(t*2.)+2.;
        //radius
        vec2 o = .25*vec2(x,y);
        //from middle to radius
        c += 0.001/(length(p-o))*vec3(0.5);
    }
    fragColor = vec4(c,1);
}
