float noise(vec3 x) {
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f * f * (3.0 - 2.0 * f);
    vec2 uv = (p.xy + vec2(37.0, 17.0) * p.z) + f.xy;
    vec2 rg = textureLod( iChannel0, (uv+.5)/256., 0.).yx;
    return mix(rg.x, rg.y, f.z);
}

float fBm(in vec3 p) {
    vec3 pos = p;
    float sum = 0.0;
    float amp = 1.0;
    for(int i = 0; i < 4; i++) {
        sum += amp * noise(p);
        amp *= 0.5;
        p *= 2.0;
    }
    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    vec3 rd = normalize(vec3(p.xy, 1.0));
    vec3 pos = vec3(0.0, 0.0, 1.0) * iTime * 0.05 + rd;
    vec3 col = vec3(0.0);

    // Domain warping:
    // https://iquilezles.org/articles/warp
    if (p.x < -0.005) {
        pos *= 8.0;
        float q = fBm(pos + vec3(8.5, 2.7, 5.3));
        float r = fBm(pos + vec3(4.6, 6.9, 2.1));
        float w = fBm(pos + 4.0 * q + 8.0 * r);
        col = vec3(0.5 * w);
    } else {
        pos *= 2.5;
        vec3 q, r;
        q.x = fBm(pos);
        q.y = fBm(pos + vec3(5.2,1.3,8.4));
        q.z = fBm(pos + vec3(2.2,5.4,7.9));
        r.x = fBm(pos + 2.0 * q + vec3(1.7,9.2,5.2));
        r.y = fBm(pos + 2.0 * q + vec3(8.3,2.8,4.8));
        r.z = fBm(pos + 2.0 * q + vec3(5.7,4.3,2.4));
        float w = fBm(pos + 4.0 * r);
        col = vec3(0.5 * w);
    }

    // the black line
    if (p.x > -0.005 && p.x < 0.005 ) {
        col = vec3(0.0);
    }

    fragColor = vec4(col, 1.0);
}
