void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float C = 0.;
    float spped = floor((cos(iTime+3.435)+1.)*4000.)+0.1;
    if(iTime < 0.1)uv.x -= 0.75;
    float t = iTime*spped;
    if(floor(mod((t*0.1),2.)) == 0.0){
        t *= -1.;
    uv.x += t;
    uv.xy *= -1.;
    uv.y = fract(uv.y);
    }else{
       uv.x += t;      
    }   

    float F = 1.0 -step( (fract(uv.x+0.5) + 1.0-uv.y),1.);
	float G = 1.0 -step( (fract(uv.x+0.5) + uv.y),1.0);
	C = G*F;

	fragColor = vec4(vec3(C),1.0);
}

