#define PI 3.14159265

float angle(vec2 v)
{
   return atan(v.y,v.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1,1,1,1);

    vec2 center = iResolution.xy / 2.0;
    vec2 dist = center - fragCoord;
    float r = length(dist);
    float theta = angle(dist)/(2.0*PI)+iTime;


    float d = 100.0;//*(.75+.25*sin(iTime));
    r = r - d*theta;
    float c = r/d - (floor(r/d));
    if (c <=.05)//+.5*sin(iTime))
    {

        fragColor = 0.0*fragColor;
    }
    else
    {
        fragColor =   1.0*fragColor;
    }


}
