void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 light;
    vec2 pos = fragCoord.xy / iResolution.xy;
    vec4 color = texture(iChannel1, pos);
    //color = vec4(1.0);
    vec4 color2 = vec4(0.2, 0.8, 0.6, 1.0);

    light.xyz = vec3(iMouse.x, iResolution.y - iMouse.y, 0.3);     
    light.y = iResolution.y - light.y;
    float dist = distance(fragCoord.xy, light.xy);

    vec3 normalvector = texture(iChannel1, pos).xyz;
    vec3 lightvector = vec3(light.x - fragCoord.x, light.y - fragCoord.y, 160.0);

    lightvector = normalize(lightvector);
    normalvector = normalize(normalvector);

    float diffuse = max(dot(normalvector, lightvector), 2.0); // 2.5 Brightness

    //fragColor = color * diffuse * (1.0 - dist / (light.z * iResolution.x)) * color2;

    if(light.z * iResolution.x > dist)
    {
        fragColor = color * diffuse * (1.0 - dist / (light.z * iResolution.x)) * color2;
    }
    else
    {
        fragColor = vec4(0);
    }

}
