float metaball(in vec2 fragCoord, in int index)
{
    float i = float(index) * 0.5;

    vec2 pos = vec2(iResolution.x*0.5 + (iResolution.x * 0.4) * sin(iTime * 0.3 * i ),
                    iResolution.y*0.5 + (iResolution.y * 0.4) * cos(iTime * 0.7 * i ));

    float radius = (iResolution.x * 0.025) + (i * (iResolution.x * 0.0125));

	return (radius / sqrt( (fragCoord.x-pos.x)*(fragCoord.x-pos.x) + (fragCoord.y-pos.y)*(fragCoord.y-pos.y) ) ); 
}

float getMetaballValue(in vec2 fragCoord)
{
    float sum = 0.0;
    for(int i = 1; i < 7; i++)
    {
        sum += metaball(fragCoord,i);
    }

	return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sum = getMetaballValue(fragCoord);

    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = clamp(sum,0.0,2.0) * vec3(uv,0.5+0.5*sin(iTime));

    col *= texture(iChannel0,uv * sum * 0.75).xyz;
    fragColor = vec4(col,1.0);
}
