void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size = 64.0;
    float halfSize = size * 0.5;
    int ix = int((fragCoord.x + halfSize) / size);
    int iy = int((fragCoord.y + halfSize) / size);

    size = size + halfSize * (0.3 * sin(float(ix) + iTime) + 0.7 * cos(float(iy) + iTime));

	float x = mod(fragCoord.x, size);
    float y = mod(fragCoord.y, size);

    if ( (x < halfSize && y < halfSize) || (x > halfSize && y > halfSize) )
    {
        fragColor = vec4(1.0,1.0,1.0,1.0);
    }
    else
    {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
