//#define COLOR
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord/iResolution.xy;
    #ifdef COLOR
    fragColor=texture(iChannel0,uv).r*vec4(uv,0.5+0.5*sin(iTime),1.0);
    #else
    fragColor=texture(iChannel0,uv);
    #endif
}

#define survive n>1 && n<4
#define born n==3
#define zoom 4.
#define noise

#define t(uv)  texture( iChannel0, (uv)/iResolution.xy ).r

int i(vec2 uv) {
    float s=0.;
    for (int i=0; i<9; i++)s+=t(uv+vec2(i%3-1,i/3-1));
	return int(s-t(uv));
}

void mainImage( out vec4 o, in vec2 uv){
    o = vec4(0.,zoom,0.,1.);
    if(distance(uv,iMouse.xy/zoom)<3.&&iMouse.z>1.)o = vec4(1.,zoom,0.,1.);
    int n = i(uv);
    if(t(uv)==1.) {
    	if(survive)o = vec4(1.,zoom,0.,1.);
    } else if(born) {
    	o = vec4(1.,zoom,0.,1.);
    }
}

#define t(x)  texture(iChannel0,x).r
#define Smooth
#define SFactor 5.
#define trail 0.8
float i(vec2 coord) {
	vec2 R = iResolution.xy;

	vec2 x = vec2(coord.x-1.,coord.y)/R;
	vec2 x1 = vec2(coord.x+1.,coord.y)/R;
	vec2 y = vec2(coord.x,coord.y-1.)/R;
	vec2 y1 = vec2(coord.x,coord.y+1.)/R;

	return t(x)+t(x1)+t(y)+t(y1);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float zoom = texture(iChannel0,vec2(0.)).g;
    vec2 co=fragCoord/zoom;
	vec2 uv = co/ iResolution.xy;
    fragColor=vec4(texture(iChannel0,uv).rrr,1.);
    #ifdef Smooth
    if(fragColor.r!=1.)fragColor=vec4(i(co)/SFactor);
    #endif
    fragColor=mix(fragColor,texture(iChannel1,fragCoord/iResolution.xy),trail);
}
