void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    //float a = uv.x * 6.28;
    //float d = uv.y - mod(iTime * 0.1, 1.0);

    vec2 mid = vec2(0.3, 0.5);
    float d = atan(uv.y-mid.y, uv.x-mid.x);
    float a = distance(uv, mid) + iTime * 0.1;

    vec2 uv2 = vec2(0.5 + d * cos(a), 
                    0.5 + d * sin(a));
    vec4 c1 = texture(iChannel0, uv2);

    mid.x = 0.7;
    d = atan(uv.y-mid.y, uv.x-mid.x);
    a = distance(uv, mid) - iTime * 0.1;
    vec2 uv3 = vec2(0.5 + d * cos(a), 
                    0.5 + d * sin(a));
    vec4 c2 = texture(iChannel0, uv3);

	fragColor = vec4(c1.r, c2.r, c1.r + c2.r, 1.0);
}
