float redShift = 100.0;
float greenShift = 50.0;
float blueShift = 15.0;
float aberrationStrength = 1.0;

float bx2(float x)
{
    return x * 2.0 - 1.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 texelSize = vec2(1.0, 1.0) / iResolution.xy;
	vec2 uv = fragCoord.xy * texelSize;
    vec2 mouse = iMouse.xy * texelSize;

    float uvXOffset = bx2(uv.x);
    float mouseXOffset = (iMouse.z > 0.0) ? bx2(mouse.x) : 0.0;

    float uvXFromCenter = uvXOffset - mouseXOffset;
    float finalUVX = uvXFromCenter * abs(uvXFromCenter) * aberrationStrength;

    float redChannel = texture(iChannel0, vec2(uv.x + (finalUVX * (redShift * texelSize.x)), uv.y)).r;
    float greenChannel = texture(iChannel0, vec2(uv.x + (finalUVX * (greenShift * texelSize.x)), uv.y)).g;
    float blueChannel = texture(iChannel0, vec2(uv.x + (finalUVX * (blueShift * texelSize.x)), uv.y)).b;

	fragColor = vec4(redChannel, greenChannel, blueChannel, 1.0);
}
