void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - iResolution.xy*.5)/ iResolution.x;
    int d = 0;
    for(int i=0;i<int(fragCoord.x);i++) {
    	d += int(texelFetch(iChannel0, ivec2(i, fragCoord.y), 0).r * 256.);
    }
    fragColor = texture(iChannel1, vec3(uv * 16.0 + vec2(float(d/256)*0.001,0),float(iFrame)/32.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float s = 0.0;
    for(float i=0.5;i<8.0;i*=2.0) {
    	float c = texture(iChannel0, vec3(fragCoord/iResolution.x,iTime*0.1)*i).r - .5;
        s += c / i;
    }
    s = clamp(s+.5,0.,1.);
    fragColor = vec4(s,s,s,0);
}
