//Chnnl 0: texture (videos best muted)
//Chnnl 1: Sound

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //define timer
    float tmr = sin(iTime);

    float scl = 3.14; //PI does nothing special, i just like it
	vec2 uv = fragCoord.xy / iResolution.xy;

    //get sound manipulation
    float tx = texelFetch(iChannel1, ivec2(uv.x+1.0,uv.y), 0).x;
    float t = float(uv.x*(scl+sin(tx)));
    float nt = float(uv.x*(scl+cos(tx)));

    //various image tiling - reacts to sound t & nt
    vec4 video = texture(iChannel0, vec2(fract(uv.x*t) - sin(iTime),uv.y*t));
    vec4 vidCopy = texture(iChannel0,fract(uv*nt) - cos(iTime));

    video = normalize((vidCopy+video))*scl;

    vec4 grad = vec4(uv,0.5+0.5*sin(tx),1.0);

    vec4 gradVid = normalize(video/grad);
    vec4 gradVid2 = normalize(grad+video)*scl;

    //switch vids
    if(tmr > 0.0)
    	fragColor = gradVid;
    else
        fragColor = gradVid2;
}
