void mainImage( out vec4 c, in vec2 fragCoord )
{
    c = texture(iChannel0,fragCoord/iResolution.xy/SCALE).araa;
    float b = texelFetch(iChannel1, ivec2(mod(fragCoord,8.)), 0).r;
    c = floor(c*4.+b)/4.;
}

#define parts 400


const float fov = 750.;

float ls(vec2 u, vec2 s, vec2 e)
{
    vec2 l1 = u-s;
    vec2 l2 = (e-s)*(clamp( dot(l1,(e-s) ) /(dot(e-s,e-s)), 0., 1. ) );

    return 1.-smoothstep(1.5,1.6,length(l1-l2));
}

float sq(vec2 f,float w,float r,float d){
    float a = 0.;
    vec3 p = vec3(1);
    float s = sin(r),c = cos(r);

    vec3 lj = vec3(0);

    for(int i = 0; i < 5; i++)
    {
       vec3 j = p*w;
       j.xz*=mat2(c,s,-s,c);
       j.z-=d;
       j.xy/=j.z;
       j.xy*=fov;
       if(i<5)
       	a+=ls(f,j.xy,j.xy*vec2(1,-1));
       if(i > 0)
       {
       	a+=ls(f,j.xy,lj.xy);
       	a+=ls(f,j.xy*vec2(1,-1),lj.xy*vec2(1,-1));
       }
       p.xz = p.zx;
       p.z*=-1.;
       lj = j;
    }

    return a;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(fragCoord.x < iResolution.x/SCALE && fragCoord.y < iResolution.y/SCALE)
    {
    vec2 fr = fragCoord + iResolution.xy/(SCALE*2.);
    vec3 col = vec3(0);

    vec2 f = 1000.*(fr -= iResolution.xy/(SCALE))/iResolution.x*SCALE;

    float r = iTime*.3+5.*sin(.3*iTime);
    float s = sin(r),c = cos(r);
    float w = smoothstep(5.,0.,iTime)*1000. + 300.;
    float d = 1600.+200.*cos(iTime*.6);
    float sb = smoothstep(3.,7.,iTime);

    col += sb*sq(f,w,r,d);

    for(int i = 0; i < parts; i++)
    {    
        vec3 dat = w*sin(vec3(5322,6344,6436)*float(i)*0.0001+.1*iTime);
        dat.xz*=mat2(c,s,-s,c);
        dat.z-=d;
        dat.xy/=dat.z;
        dat.xy*=fov;
        float b = .19*smoothstep(0.,3.,iTime);
        if(dat.z < 0.)
        {
        	col += b*(1.+( max((dat.z+1500.)/500.,-.6) ) )/(length(f-dat.xy));//*(.5+.5*sin(vec3(2.,1.4,1.8)*dat.w+iTime));
            dat.xy *= 3.4;
            col += b*(1.+( max((dat.z+1500.)/500.,-.6) ) )/(length(f-dat.xy));
        }
    }

	fragColor = vec4(pow(col,vec3(4)),0)+.3*texture(iChannel0,fragCoord/iResolution.xy);
    }
}
