const int MAX_ITER = 100; // 100 is a safe number to use, it won't produce too many artifacts and still be quite fast
const float MAX_DIST = 200.0; // Make sure you change this if you have objects farther than 20 units away from the camera
const float EPSILON = 0.001; // At this distance we are close enough to the object that we have essentially hit it

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


float sphere(vec3 pos, float radius)
{
    return length(pos) - radius;
}

float box(vec3 pos, vec3 size)
{
    return length(max(abs(pos) - size, 0.0));
}

float distfunc(vec3 pos)
{

    pos.x = mod(pos.x,10.0) - 5.0;
    pos.y = mod(pos.y,10.0) - 5.0;
    pos.z = mod(pos.z,10.0) - 5.0;

    float value =0.0;

    value = sphere(pos,2.0);

    return value;
}

vec4 evaluate(vec2 uv)
{

    vec3 cameraOrigin = vec3(cos(iTime*0.02)*50.0,cos(iTime*0.05)*50.0,sin(iTime*0.02)*50.0); //vec3(2.0, 7.0, 2.0);
	vec3 cameraTarget = vec3(0.0, 0.0, 0.0);
	vec3 upDirection = vec3(0.0, 1.0, 0.0);
	vec3 cameraDir = normalize(cameraTarget - cameraOrigin);
	vec3 cameraRight = normalize(cross(upDirection, cameraOrigin));
	vec3 cameraUp = cross(cameraDir, cameraRight);

     float totalDist = 0.0;
	vec3 pos = cameraOrigin;
	float dist = EPSILON;



    vec3 rayDir = normalize(cameraRight * uv.x + cameraUp * (uv.y) + cameraDir);

    vec4 color = vec4(0.0,0.0,0.0,0.0);

    bool hasHitOnce = false;

    int hitTimes = 1;

    for (int i = 0; i < MAX_ITER; i++)
	{

        if(totalDist>MAX_DIST)
            break;


    		// Either we've hit the object or hit nothing at all, either way we should break out of the loop
   		 if ((dist < EPSILON) )
   		 {
        	vec2 eps = vec2(0.0, EPSILON);
			vec3 normal = normalize(vec3(
    		distfunc(pos + eps.yxx) - distfunc(pos - eps.yxx),
   			distfunc(pos + eps.xyx) - distfunc(pos - eps.xyx),
   			distfunc(pos + eps.xxy) - distfunc(pos - eps.xxy)));


            //let's fuck with the normals a bit
             normal.x += texture(iChannel0, vec2(pos.x,pos.z)*0.1).x*0.1;
             normal.y += texture(iChannel0, vec2(pos.y,pos.z)*0.1).x*0.1;
             normal.z += texture(iChannel0, vec2(pos.z,pos.x)*0.1).x*0.1;

   			float diffuse = max(0.0, dot(-rayDir, normal));
    		float specular = pow(diffuse, 64.0);
        	float value = diffuse*0.6;
             vec4 hitcolor = hsv2rgb(vec3(totalDist/100.0,1.0,1.0)).xyzz*value + specular*0.002;
    		//color += hsv2rgb(vec3(totalDist/100.0,1.0,1.0)).xyzz*value;
      		//color = color+specular*0.002;

             if(hitTimes<3)
             color += hitcolor/float(hitTimes);
       	 	hasHitOnce = true;

            rayDir = 2.0 * dot(rayDir,normal)*normal - rayDir;
            pos += rayDir*5.0;
             totalDist = 0.0;
			 dist = EPSILON;

             hitTimes++;
            // if(hitTimes==3)
            //     break;
   		 }


        pos+=  cos(pos*0.2)*0.9;


    	dist = distfunc(pos); // Evalulate the distance at the current point
    	totalDist += dist;
    	pos += dist * rayDir; // Advance the point forwards in the ray direction by the distance
	}

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



	vec2 uv = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    uv*=0.1; //FOV
    uv.x*= iResolution.x /iResolution.y;


   vec4 col = evaluate(uv);

       //  uncomment for smoother rendering if your pc can take it.. mine can't
    /*
   vec4 col = (evaluate(uv) + evaluate(uv + vec2(0.0001,0.0))
       + evaluate(uv + vec2(-0.0001,0.0))
       + evaluate(uv + vec2(0.0,0.0001))
       + evaluate(uv + vec2(0.0,-0.0001))
       + evaluate(uv + vec2(0.0001,-0.0001))
       + evaluate(uv + vec2(-0.0001,0.0001))
       + evaluate(uv + vec2(0.0001,-0.0001)))/8.0;
    */

		fragColor = col;



}
