vec3 rgb(int a, int b, int c) {
	return vec3(float(a)/255.,float(b)/255.,float(c)/255.);    
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 col = vec3(.1, .45, .6);

    float a = (sin(uv.x*2.+iTime-cos(iTime+uv.x*10.+sin(iTime+uv.x*5.)*3.14159*.5)*3.14159)*.02+.2-uv.y);

    col-= length(uv-.5)/1.62/3.;
    if (a>.02)
    {
        col = rgb(64, 164, 223);

    } else if (a>0.) {
    	col = rgb(64, 164, 233)*1.2;   
    }

    fragColor = vec4(col, 1);
}
