float velocity = 0.5;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    float v = 0.0;
    for(float i = 0.0; i<5.0; i++){
        vec2 c = 0.8 * sin(velocity*iTime*1.0 + vec2(i,0.5) + i);
    	v += 1.0 - smoothstep(0.0,0.6,length(p - c));
    }

    // colo
    vec3 col = vec3(smoothstep(0.76,0.8,v));

	fragColor = vec4(col,1.0);
}
