vec2 pos(vec2 uv, float t)
{
    return vec2(atan(asin(uv.x), sin(uv.y)))*mat2(cos(t),-sin(t),sin(t),cos(t));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0  - 1.0;
	fragColor = texture(iChannel0, pos(uv, iTime/24.));
    fragColor += length(cos(fragColor.r + uv.y)) - vec4(0.1, 0.4, 1.0, 1.0);
    if(vec2(length(uv)-0.25).r < 0.1)fragColor += 0.15;
    else if(vec2(length(uv)-0.3).r < 0.1)fragColor += 0.1;
    else if(vec2(length(uv)-0.4).r < 0.1)fragColor += 0.075;
    else if(vec2(length(uv)-0.55).r < 0.1)fragColor += 0.065;
    else if(vec2(length(uv)-0.85).r < 0.1)fragColor += 0.05;
    else if(vec2(length(uv)-0.985).r < 0.1)fragColor += 0.035;
    else if(vec2(length(uv)-1.109).r < 0.1)fragColor += 0.025;
    else if(vec2(length(uv)-1.186).r < 0.1)fragColor += 0.0185;
    else if(vec2(length(uv)-1.201).r < 0.1)fragColor += 0.01;
    else fragColor += 0.005;
}

float TAU = asin(1.)*4.;
float TWOTAU = asin(1.)*8.;
vec2 mainSound( in int samp, float time )
{
    return vec2( sin(dot(TWOTAU,TAU)*440.0*fract(time))*exp(-3.0*fract(time)) );
}
