void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	float t = iTime;
    float t1 = t * -0.05;
    float l = mod(uv.x+t1,.3);
    vec4 c = vec4(1,1,1,1);
    float co = 5.;
    float b = 0.001;
    float w = 0.008;
    float s = clamp(l, b, w)*co;
    fragColor = c*s*20.;
}
