#define TILES

const float denst = 25.0;
const float kinds = 25.0;
const float patternDenst = 4.0;

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float fcx = sin(fragCoord.x / denst);
    float fcy = cos(fragCoord.y / denst);
    vec2 fv = floor(fragCoord.xy / denst);
	float prev = rand(fv + vec2(-1.0, 0.0));
    float cur = rand(fv);
    cur *= mix(.6, .8, (sin(iTime) + 1.0) / 2.0);

    if(prev < cur)
    {
        cur = prev;
    }
    cur = floor(cur * kinds) / kinds;

    //single tile
#ifdef TILES
    float mixed = mix(0.1, 0.4, cur);
    float toSin = fragCoord.x * mixed * patternDenst;
    float toCos = fragCoord.y * mixed * patternDenst;
    if(cur > .5)
    {
        float dir = cur < .6 ? -1.0 : 1.0;
        cur = sin(toSin + toCos * dir);
    }
    else if(cur > .3)
    {
        cur = sin(toSin) * cos(toCos);
    }
    else if(cur > .2)
    {
        cur = sin(fragCoord.x)+sin(fragCoord.y);
    }

#endif
    if(cur > 0.15)
    {
        cur = 1.0;

        fragColor = vec4(vec3(0.3,0,0), 1.0);
    }
    else
    {
         fragColor = vec4(mix(vec3(1,1,0), vec3(0), vec3(cur)), 1.0);
    }

}
