void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2 (fragCoord.xy - 0.5*iResolution.xy);
    uv.xy = 2.0 * uv.xy/iResolution.y;

    vec4 black =  vec4 (0.0,0.0,0.0,1.0);
    vec4 aColor =  vec4 (0.6,0.8,0.1,1.0);
    vec4 bColor =  vec4 (0.8,0.4,0.1,1.0);
    vec4 cColor = vec4(0.216, 0.471, 0.698,1.0);

    float rad = 0.8;

    fragColor = black;

    vec2 center1 = vec2 ((0.1*cos(float(iFrame)/50.0)) ,(0.1*sin(float(iFrame)/50.0)));

    vec2 d = uv - center1;

    if (length(d) < 0.8) fragColor = aColor;

    float x = 1.0/6.0;
    float w = 0.01;

    //line 1
    if (uv.x < -2.0*x && uv.x > -2.0*x-w)
        fragColor = vec4(1.0);    

    //line 2
    if (uv.x > 2.0*x && uv.x < 2.0*x+w)
        fragColor = vec4(1.0);


    vec2 center2 = vec2 ((0.1*sin(float(iFrame)/60.0)) ,(0.1*cos(float(iFrame)/50.0)));

    d = uv - center2;

    if (length(d) < 0.3)
        fragColor = bColor;

    vec2 center3 = vec2 (0.8*sin(float(iFrame)/20.0)*2.0 ,cos(float(iFrame)/100.0)*0.8 + 0.2*(sin(float(iFrame)/8.0)));

    d = uv - center3;

    if (length(d) < 0.6) fragColor = cColor;

    x = 1.0/1.5;    
}
