vec2 checker(vec2 p) {
    vec2 p2 = abs(fract(p)-0.5);
    return vec2(mod(floor(p.x)+floor(p.y), 2.0),max(1.0-(-max(p2.x,p2.y)+0.5)*50.0,0.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
    fragColor = vec4(1.0);


    float len = 0.0;
    for ( float i = 5.0; i< 30.0; i++) {

        float size = i;

        vec2  pos = (2. * fragCoord.xy / iResolution.xy - 1.)
                * vec2(iResolution.x / iResolution.y,1.);
        vec2  rot = vec2(atan(pos.x, pos.y) / 3.14*size+iTime/(size-4.0),
                         size / length(pos)*0.1+iTime+i*0.2);
        vec2 check = checker(rot);
        if (check.x == 1.0) {
    		fragColor = vec4(0.05*i);
            break;
        }
        len = max(check.y,len);

    }

}
