void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-iResolution.xy*0.5) / iResolution.y;

    vec3 color = vec3(1.0);

    float r = fract(iTime*0.2);

    float uv_r = length(uv);

    float thickness = 0.04;

    float aa = 1./iResolution.y;

    color = color * ((smoothstep(r+thickness+aa, r+thickness, uv_r)) * smoothstep(r-aa, r, uv_r)); 

    fragColor = vec4(color, 1.0);
}
