void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy;

    vec2 mouse_uv = mouse / iResolution.xy;

    if (fragCoord == vec2(0.0)) {
        mouse_uv = vec2(0.75, 0.2);
    }


	vec2 uv = fragCoord.xy / iResolution.xy;

    if (uv.x > 0.5)
        uv.x = 1.0 - uv.x;

    uv.x = sqrt(uv.x);
    uv += mouse_uv;


    vec4 tex_color = texture(iChannel0, uv);

    fragColor = tex_color * vec4(0.0, 0.5, 1.0, 1.0) + 0.05;
}
