void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //this code was taken from a code sample I found online
    //I think it's supposed to act like a greenscreen

	vec2 xy = fragCoord.xy / iResolution.xy;
    vec4 texColorA = texture(iChannel0, xy);
    vec4 texColorB = texture(iChannel1, xy);

    if((texColorA.r + texColorA.b) < texColorA.g){	// checks for one color in the image
    	fragColor = texColorB;	//show second image when including green
    }
    else
    {
        fragColor = texColorA;	//show the first image while exclusing green 
    }
}
