float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233 * sin(iTime)))) * 43758.5453);
}

float backColor(vec2 coord) {
    return texture(iChannel0, coord/iResolution.xy).r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,0.0,1.0);

    vec2 coords = vec2(fragCoord.x, iResolution.y - fragCoord.y);

    if (coords.y > iResolution.y - 1.0) {
     fragColor.rgb = vec3(rand(fragCoord), rand(fragCoord), rand(fragCoord));
    } else {
    	 vec2 leftCoord = (fragCoord - vec2(1.0, 1.0));
        vec2 midCoord = (fragCoord - vec2(0.0, 1.0));
        vec2 rightCoord = (fragCoord - vec2(-1.0, 1.0));
        vec2 oppCoord = (fragCoord - vec2(0.0, 2.0));
       fragColor.r = (backColor(leftCoord) + 
             backColor(midCoord) + 
              backColor(rightCoord) + 
              backColor(oppCoord)) / 4.05;
        fragColor.g = fragColor.r / 1.5;
    }
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233 * sin(iTime)))) * 43758.5453);
}

vec3 backColor(vec2 coord) {
    return texture(iChannel0, coord/iResolution.xy).rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
       vec2 uv = fragCoord/iResolution.xy;


    vec3 video = texture(iChannel1, uv).rgb;
    vec3 fire = backColor(fragCoord);

    vec3 col = 1.2 * fire / video;


    // Output to screen
    fragColor = vec4(col,1.0);
}
