#define N 48                        // simulates N*N particles

#define S(v) smoothstep(1.,0.,abs(v))

void mainImage( out vec4 O, vec2 u )
{
    O -= O;
    vec2 R = iResolution.xy,
         U = floor(u - R/2.), P;
    float d = 0., p = 0.;

    for (int i=0; i<N*N; i++) {       // seek for dots reaching the pixel
        P = texelFetch(iChannel0,ivec2(i%N,i/N),0).xy;
      //if ( P == U ) O++;
        if (length(P-U) < .5) O++;             // draw dot
        if ( abs(P-U).x < .5) p++;             // horiz proj
        if (round(length(P))==floor(u.x)) d++; // bin u.x for stat of distances
    }

    // --- display stats
    float l = length(U), t = float(iFrame);
    O.b += S(l-t);                             // max reachable dist
    O = mix( O, vec4(1,0,0,0), S(l-sqrt(t)) ); // average dist
    if (u.y < p) O.b++;                        // horiz proj
    if (abs(u.y - 1./sqrt(3.14*t) * exp(-U.x*U.x/t)*float(N*N)) < .5 ) O.bg++; // Gaussian

    if (u.y < d) O.g++;                        // dist histogram
    if (abs(u.y - 2.*u.x/t * exp(-u.x*u.x/t)*float(N*N)) < 1. ) O.r++; // Rayleigh
}

#define rnd(x) fract(3e4*sin((x+iTime)*mat2(1,13.51,73.37,-57.17)))

void mainImage( out vec4 O,  vec2 U )
{
    O = texelFetch( iChannel0, ivec2(U), 0 );

#if 0                              // x-y separable random walk

    O.xy += sign(2.*rnd(U)-1.);              

#else                              // isotropic random walk

    float a = 6.28319 * rnd(U).x;
    O.xy += vec2(cos(a),sin(a));

#endif
}
