float rows = 20.0; //amount of rows
float cols = 10.0; // amount of columns
vec4 board(vec2 uv, vec4 rowColor, vec4 columnColor);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec4 color1 = vec4(1.0,1.0,1.0,1.0);//makes a white color
    vec4 color2 = vec4(0.0,0.0,0.0,0.0);//makes a black color
	fragColor = board(uv, color1 , color2);
}

vec4 board(vec2 uv, vec4 color1, vec4 color2)
{
    vec4 colorReturn;
    float rowOne = mod(floor(uv.x * rows), 2.0);
    float lastRow = mod(ceil(uv.x * rows), 2.0);
    float colNum = mod(floor(uv.y * cols),2.0);

    if(rowOne == 0.0 && colNum == 1.0)//ensures that the spaces are consistent in size
	{
		colorReturn = color1;
	}
    else if (lastRow == 0.0 && colNum == 0.0)//ensures that the spaces are consistent in size
    {
        colorReturn = color1;
    }
    else
        colorReturn = color2;
    return colorReturn; //returns the color to be used
}
