// Inverse bilinear interpolation: given four points defining a quadrilateral, compute the uv
// coordinates of any point in the plane that would give result to that point as a bilinear 
// interpolation of the four points.
//
// The problem can be solved through a quadratic equation. More information in this article:
//
// https://iquilezles.org/articles/ibilinear

const float c_textureSize = 8.0;
#define c_onePixel  (1.0 / c_textureSize)
#define c_twoPixels  (2.0 / c_textureSize)

vec4 NearestTextureSample ( vec2 P)
{
    vec2 pixel = P * c_textureSize;

    vec2 frac = fract(pixel);
    pixel = (floor(pixel) / c_textureSize);
    return texture(iChannel0, pixel + vec2(c_onePixel/2.0));
}

//modified from https://www.shadertoy.com/view/ltfXWS, tested a few different versions of this same function and this one seemed to have the nicest results
vec4 NearestTextureSample2DAA(vec2 uv) {
    vec2 texsize = vec2(textureSize(iChannel0,0));
    vec2 uv_texspace = uv*texsize;
    vec2 seam = floor(uv_texspace+.5);
    uv_texspace = (uv_texspace-seam)/fwidth(uv_texspace)+seam;
    uv_texspace = clamp(uv_texspace, seam-.5, seam+.5);
    return texture(iChannel0, uv_texspace/texsize);
}


float cross2d( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

// given a point p and a quad defined by four points {a,b,c,d}, return the bilinear
// coordinates of p in the quad. Returns (-1,-1) if the point is outside of the quad.
vec2 invBilinear( in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d )
{
    //!!!!!!!!!!!!!!!!!!!!!!!!
    /*fix from page https://www.shadertoy.com/view/lsBSDm
		Hackerham, 2017-08-07
		Made a fix: https://gist.github.com/ivanpopelyshev/2a75479075286deb8ee5dc1fb2e07f09
	*/
    //!!!!!!!!!!!!!!!!!!!!!!!!
    vec2 e = b-a;
    vec2 f = d-a;
    vec2 g = a-b+c-d;
    vec2 h = p-a;

    float k2 = cross2d( g, f );
    float k1 = cross2d( e, f ) + cross2d( h, g );
    float k0 = cross2d( h, e );

    float k2u = cross2d( e, g );
    float k1u = cross2d( e, f ) + cross2d( g, h );
    //float k0u = cross2d( h, f);    

    float v1, u1, v2, u2;

    float w = k1*k1 - 4.0*k0*k2;

    w = sqrt( w );

    v1 = (-k1 - w)/(2.0*k2);    
    u1 = (-k1u - w)/(2.0*k2u);        
    if(v1>0.0 && v1<1.0 && u1>0.0 && u1<1.0) 
        return vec2( u1, v1 );

    v2 = (-k1 + w)/(2.0*k2);
    u2 = (-k1u + w)/(2.0*k2u);       
    if(v2>0.0 && v2<1.0 && u2>0.0 && u2<1.0) 
        return vec2( u2, v2 );

    return vec2(-1.0);
}

vec2 invBilinear2( in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d )
{
    vec2 e = b-a;
    vec2 f = d-a;
    vec2 g = a-b+c-d;
    vec2 h = p-a;

    float k2 = cross2d( g, f );
    float k1 = cross2d( e, f ) + cross2d( h, g );
    float k0 = cross2d( h, e );    

    float w = k1*k1 - 4.0*k0*k2;
    w = sqrt( w );

    float ik2 = 0.5/k2;
    float v = (-k1 - w)*ik2;
    float u = (h.x - f.x*v)/(e.x + g.x*v);

    if( u>=0.0 && u<=1.0 && v>=0.0 && v<=1.0 )
        return vec2( u, v );

    v = (-k1 + w)*ik2;
    u = (h.x - f.x*v)/(e.x + g.x*v);
    return vec2( u, v );    
}


float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p - a;
	vec2 ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

vec3  hash3( float n ) { return fract(sin(vec3(n,n+1.0,n+2.0))*43758.5453123); }

//added for dithering
bool even(float a) { return fract(a/2.0) <= 0.5; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;

    // background
    vec4 bg = vec4(sin(iTime+(fragCoord.y * .01)),sin(3.0*iTime+2.0+(fragCoord.y * .01)),sin(5.0*iTime+4.0+(fragCoord.y * .01)), 1.0);
    vec4 col = bg;


    // move points
    vec2 a = sin( 0.11*iTime + vec2(0.1,4.0) );
    vec2 b = sin( 0.13*iTime + vec2(1.0,3.0) );
    vec2 c = cos( 0.17*iTime + vec2(2.0,2.0) );
    vec2 d = cos( 0.15*iTime + vec2(3.0,1.0) );

    // area of the quad
    vec2 uv = invBilinear2( p, a, b, c, d );
    if( uv.x>=0.0 && uv.y>=0.0 && uv.x<=1.0 && uv.y<=1.0)
    {
        col = NearestTextureSample2DAA( uv ).rrrr;
    }

    fragColor = col;
}
