void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    float color_resolution = 1.0/3.0;
    col = col - mod(col, vec3(color_resolution)) + 0.5 * color_resolution;
    // Output to screen
    fragColor = vec4(smoothstep(0.0, 1.0, col),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float bright = dot(vec3(0.299, 0.587, 0.114), texture(iChannel0, uv).rgb);
    bright += 0.0 * (texture(iChannel2, mod(4.0 * uv, vec2(1.0))).r - 0.5);
    vec3 last_color = texture(iChannel1, uv).rgb;
    float g = bright;
    float r = mix(last_color.r, last_color.g, 0.5 * step(mod(iTime, 0.125), 0.03725));
    float b = mix(last_color.b, last_color.r, 0.5 * step(mod(iTime, 0.125), 0.03725));
    fragColor = vec4(r,g,b,1.0);
}
