float sd_triangle(vec2 p, float size) {
    p *= 0.84;
    p.y += 0.17;
	return max(abs(p.x * 1.5) + p.y, -p.y * 1.5) - size;
}

float sd_circle(vec2 p, float size) {
	return length(p) - size;
}

#define sd_line(sd, width) (max(sd, -sd) - width)

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    uv -= vec2(0.0, 0.1);


    float width = clamp(0.02 + sin(iTime) * 0.1, 0.01, 0.03);
    float triangles = -0.75;

    float sd = sd_line(sd_triangle(uv + vec2(triangles, -0.1), 0.5), width);
    sd = min(sd, sd_line(sd_triangle(uv + vec2(triangles, 0.1), 0.5), width));

    sd = min(sd, sd_triangle(uv + vec2(triangles, 0.1), 0.5));

    float circles = 0.75;

    sd = min(sd, sd_line(sd_circle(uv + vec2(circles, 0.1), 0.6), width));
    sd = min(sd, sd_circle(uv + vec2(circles, 0.1), 0.5));

    float r = 2000.0 / (iResolution.x * iResolution.y);
    vec3 col = vec3(smoothstep(-r, r, sd));
//    vec3 col = vec3(sd);

    fragColor = vec4(col,1.0);
}
