// Sphere - Signed Distance Function
float sphere(vec3 p, float radius){
    return length(p)-radius;
}




// Locate objects
float map(vec3 p)
{    
    // Sphere radius
    float sphereSize = 0.7;

    // Transform coordinate space so spheres repeat
    vec3 q = fract(p*sin(iTime)) * cos(iTime);

     int tx = int(q.x);
    float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 
	fft *= 5.;
    // Signed distance of sphere
    float s = sphere(q, sphereSize);

    float d = 0.1 * (cos(q.x*5.*fft) * cos(q.y*5.*fft) * cos(q.z*5.*fft) );
    //return s +wave;
    return s+d;
}


// Trace rays
float trace(vec3 origin, vec3 r) 
{
  float t = 0.0; // Distance Traveled
    for (int i = 0; i < 32; ++i) {
        vec3 p = origin + r * t;
        float d = map(p); // Locate object
        t += d * cos(iTime)*10.; // Step along the ray
    }
    return t; 
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(tan(iTime), cos(iTime*0.4), -sin(iTime)+3.);

    uv = uv *2.-1.; // Remap the space to -1. to 1.
    uv.x *= iResolution.x/iResolution.y;



   	// Create ray to fire into scene
   	vec3 ray = normalize(vec3(uv, 1.0));

    // Create origin of scene
    vec3 origin = vec3(0., 0.,iTime);


    // Trace any objects in the scene
    float t = trace(origin*cos(iTime), ray*-sin(iTime));

    t += smoothstep(-sin(iTime),-tan(iTime),cos(iTime));

    // Generate fog based on distance from t
    float fog = 10. / (1.0 + t * t * 0.3);
    float expFog = 2.0 / (t*t*0.1);
    // Final color with includes sdf + fog
    vec3 fc = vec3(expFog*7.);
     //vec3 fc = vec3(expFog*7.,iTime/uv.xy-vec2(-cos(iTime),0));

    //fc /= cos(log(color*fft)*0.5);
    fc -= tan(color);
    // Output to screen
    fragColor = vec4(-tan(fc*0.7)+-sin(fc*0.7),cos(fc*0.3));
}
