vec2 pos = vec2 (.5, .5);
float radius = .125;
float freq = 3.0;
float amp = .3;
float change = .5;
vec4 newColor = vec4(.5, 0.0, 0.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspectRatio =iResolution.y / iResolution.x;
    uv.y = uv.y * aspectRatio;
    pos.y *= aspectRatio;

    float colorVal = abs(sin(iTime*freq) * change);


    newColor +=vec4(colorVal, colorVal, colorVal, 0.0);



    pos.y += sin(iTime* freq) * amp;

    if(length(pos - uv) < radius)
    {
        fragColor = newColor;
    }
	else
	{
    	fragColor = vec4(0.0,0.0,0.0,1.0);
	}
}
