// parameters
#define BOX_SIZE      18.0
#define LINE_WIDTH     1.5
#define RENDER_CROSS   1
#define MOUSE_AMOUNT   0.0
#define LINE_LENGTH    6.0

// careful with this value. iterations = (2n + 1)^2
// needs to be large enough to fit LINE_LENGTH.
#define offsetSize     3.0



// not parameters
#define LARGE_VALUE 100000000.0
#define STRAIGHT       1

float oxeVec2f_distanceRight_PointLine(vec2 linePt, vec2 unitDir, vec2 pt)
{
   float vx = pt.x - linePt.x;
   float vy = pt.y - linePt.y;
   return unitDir.y * vx - unitDir.x * vy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float boxDim = BOX_SIZE;
    vec2 lineCenterC = vec2(boxDim, boxDim) * 0.5;
    vec2 fc2 = fragCoord - (iResolution.xy * 0.5);
    vec2 pt  = mod(fc2, boxDim);

    #if STRAIGHT
    vec2 boxPosC = (floor(fc2 / boxDim) + vec2(0.5)) * boxDim + iResolution.xy * 0.5;
    #else
    vec2 boxPosC = fc2;
    #endif

    vec3 col = vec3(0.);

    float r = LINE_LENGTH;

    for (float dx = -offsetSize; dx <= offsetSize; dx += 1.0) {
	    for (float dy = -offsetSize; dy <= offsetSize; dy += 1.0) {
            vec2 offset = vec2(dx, dy) * boxDim;
            vec2 lineCenter = lineCenterC + offset;
            vec2 boxPos = boxPosC + offset;
		    float boxDist1 = length(boxPos - iResolution.xy * 0.5) *  1.0;
		    float boxDist2 = length(boxPos - iMouse.xy)            * -MOUSE_AMOUNT;
			float boxDist = boxDist1 + boxDist2;

		    float t = iTime * 1.4 * (boxDist * 0.003 + 0.2);
            vec2 lineDir = vec2(cos(t), sin(t));

		    float distN = length(pt - lineCenter) / (boxDim * 0.5);

            float d = 0.0;
            float sd;
            sd = oxeVec2f_distanceRight_PointLine(lineCenter, lineDir, pt);
            d  = max(d, smoothstep(LINE_WIDTH, 0.0, abs(sd)));

            #if RENDER_CROSS
            lineDir = vec2(-lineDir.y, lineDir.x);
            sd = oxeVec2f_distanceRight_PointLine(lineCenter, lineDir, pt);
            d  = max(d, smoothstep(LINE_WIDTH, 0.0, abs(sd)));
            #endif // RENDER_CROSS

            // restrict to a circle
            d *= smoothstep(r * 1.0, r * 0.5, distN);
            // mute the middle
            d *= smoothstep(r * 0.0, r * 0.2, distN);


            // grey in the middle
            // d *= smoothstep(0.0, 0.8, distN) * 0.5 + 0.5;

            col.xyz += d;
   		    // col.z   = boxDist / iResolution.y;
        }
    }



    // Output to screen
    fragColor = vec4(col,1.0);
}
