{
	fragColor = pow(texture(iChannel0, fragCoord.xy / iResolution.xy),vec4(2));
}

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = vec4(0);
    float t0 = iTime + hash33(vec3(fragCoord.xy,iFrame)).z/60.;
	vec2 uv = ((fragCoord.xy+hash33(vec3(fragCoord.xy,iFrame)).xy)-iResolution.xy/2.) / iResolution.xx;
    uv *= mat2(sin(.002*t0*t0),cos(.002*t0*t0),cos(.002*t0*t0),-sin(.002*t0*t0));
    vec3 p = .1*t0*t0*vec3(.35, .47, .79)+vec3(.7, .3, .4);
    float l = 0.0;
    vec3 d = vec3(.36, .48, .80);
    vec3 r = vec3(0.48, -0.36, 0);
    vec3 rd = normalize(r*uv.x+cross(r,d)*uv.y + d);
    for(int i = 0; i < 80; i++){
        float closest = 1.;
        for(int nx = -1; nx < 1; nx++){
        for(int ny = -1; ny < 1; ny++){
        for(int nz = -1; nz < 1; nz++){
            vec3 np = ceil(p+l*rd) + vec3(nx,ny,nz);
            np += (hash33(np)-.5);
            closest = min(closest, length(p+l*rd-np));
        }
        }
        }
        l += closest;
        fragColor += .0002/(closest+.0001)*vec4(pow(hash33(ceil(p+l*rd)),vec3(.005*iTime)),1);

    }
    l=(l*l+5.)/(l+1.);
	fragColor += (1.-10./(iTime*iTime*.1+20.))*texture(iChannel0,(1. - 0.*iTime/40000.)*(fragCoord.xy-iResolution.xy/2.)/iResolution.xy+iResolution.xy/2./iResolution.xy);
    fragColor *= .98;
    fragColor = min(fragColor, vec4(1));
}
