#define T(c,x) ivec4( texture(c, U - iTime*vec2(x,0) ) *255. )

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;

    ivec4 t0 =            T(iChannel0,.1),
          t1 = U.y > .5 ? T(iChannel1,.0) : ivec4(U.x*255.);

    O = vec4(  t0 ^ t1 ) / 255.;
  //O = vec4(  t0 & t1 ) / 255.;
  //O = vec4(  t0 | t1 ) / 255.;
  //O = vec4( (t0 + t1) %256 ) / 255.;
}
