const vec4 colorBg = vec4( 18,  3, 35, 1) / 255.,
           colorA  = vec4(250, 47, 63, 1) / 255.,
           colorB  = vec4( 62, 24, 66, 0) / 255.;

vec2 plot(float st, vec2 pctA, vec2 pctB)
{
  return  smoothstep( -.02, 0., st-pctA) -  smoothstep( -.02, 0., st-pctB);
}

void mainImage( out vec4 O,  vec2 U )
{
	U /= iResolution.xy;

    float tsin = (sin(iDate.w) + 1.) / 2.;

    vec2 X  = vec2( U.x, 1.-U.x ),
         ya = X *  tsin,
         yb = X * (tsin + 1.),
         p  =  plot(U.y,ya,yb);


    vec4 cl = p.x * mix(colorA, colorB, X.x),
         cr = p.y * mix(colorA, colorB, X.y);

    O = (cl + cr) + colorBg;
}
