void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy - .5) * vec2(iResolution.x / iResolution.y, 1.);

    float R = length (uv);
    float A = atan (uv.y / uv.x);

    // sample some noise
	vec4 n4 = texture (iChannel0, vec2 (R * 5. - iTime * .1, A));
    float n1 = 4. * clamp(n4.r - .1, 0.,1.) * clamp(1.5 - 3. * R, 0.,1.);

    n1 *= n1 * n1;

    fragColor = vec4 (mix (vec3 (.4,.4,.9), vec3 (.9,.7,.4), n1), 1.);

    fragColor.rgb += vec3 (max (11.5 - 50. * R, 0.));
}
