vec3 h(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 v = (fragCoord.xy / iResolution.y-.5)*4.*vec2(.5, 1);
    vec2 c = v/exp2(texture(iChannel0, vec2(0)).w)+2.*(texture(iChannel0, vec2(0)).rg-1.),z = vec2(0);
    float r = 0.;
    for (int i = 0; i < 200; i++){
        if (length(z)>=2.) {

            break;
        }


        r++;
        z=vec2(z.x*z.x-z.y*z.y+c.x,
               dot(z,z.yx)+c.y);
    }
    fragColor = vec4(length(z)<2.?vec3(0):h(vec3(mod(256., r)/2.56,1.,1.)), 0.)*(1.-length(fragCoord/iResolution.xy-.5)/1.62);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 dm = iMouse.xy/iResolution.xy-.5;
    dm/=10.;
    fragColor = max(vec4(0.),texture(iChannel0, vec2(0.))+vec4(clamp(iMouse.w,0.,1.)*dm/exp2(max(1.,texture(iChannel0,vec2(0.)).w)), 0.,(iMouse.w>0.?.03:-.02)));
}
